class Solution(object):
    def numMatchingSubseq(self, S, words):
        """
        :type S: str
        :type words: List[str]
        :rtype: int
        """
        res = []
        maping = [-1 for _ in range(26)]
        for i in range(len(S)):
            if maping[ord(S[i]) - ord('a')] == -1:
                maping[ord(S[i]) - ord('a')] = 1
            else:
                maping[ord(S[i]) - ord('a')] += 1
        for word in words:
            # Wierdo python! you can not copy the values by = sign it just reference based
            # So each time you change the temp values the maping values are also going to change
            # https://stackoverflow.com/questions/2612802/how-to-clone-or-copy-a-list
            temp = list(maping)
            for ch in word:
                temp[ord(ch) - ord('a')] -= 1
            counter = 0
            for ch in word:
                if temp[ord(ch) - ord('a')] >= 0:
                    counter += 1
            if counter == len(word):
                res.append(word)
        return len(res)

